import React, { Component } from "react";
import api from "../../services/api";
import { Link } from "react-router-dom";

// import "./styles.css";

export default class Team extends Component {
    state = {
        teams: [],
    };

    componentDidMount() {
        this.loadTeam();
    };

    loadTeam = async () => {
        const { nickName } = this.props.match.params; 

        const response = await api.get(`teams/nickName/${nickName}`);
        this.setState({teams: response.data.api.teams});
        
    };

    render() {

        const { teams } = this.state;

        const team = teams.map(team);

        return (
            <div className="teamsLeague-page">
                <div className="teams">
                    <h2>Teams</h2>
                    <h1>{team.fullName}</h1>
                    {/* {teams.map(team => (
                        <Link to={`/team/${team.nickname}`} key={team}>
                            <strong>{team.fullName}</strong>
                            <img src={team.logo}/>
                        </Link>
                    ))} */}
                </div>
            </div>
        );
    }
};
