import React, { Component } from "react";
import api from "../../services/api";

import "./styles.css";

export default class Team extends Component {
    state = {
        teams: [],
    };

    componentDidMount() {
        this.loadTeam();
    };

    loadTeam = async () => {
        const { nickName } = this.props.match.params; 

        const response = await api.get(`teams/nickName/${nickName}`);
        this.setState({teams: response.data.api.teams});
    };

    render() {
        const {teams} = this.state;

        return (
            <div className="team-page">
                {teams.map(team => (
                    <article key={team}>
                        <div className="infos">
                            <h3>{team.fullName}</h3>
                            <ul>
                                <p>Nome: {team.fullName}</p>
                                <p>Apelido: {team.nickname}</p>
                                <p>Cidade: {team.city}</p>
                                <p>Abreviação: {team.shortName}</p>
                                <p>Ligas Disputadas: </p> 
                                <ul>{
                                    Object.keys(team.leagues).map(league => (
                                        <p>{league}</p>
                                    ))
                                }</ul>
                            </ul>
                        </div>
                        <div className="logo">
                            <img alt={`logo do time ${team.fullName}`} src={team.logo}/>
                        </div>
                    </article>
                ))}
            </div>
        );
    }
};
