import React, { Component } from "react";
import api from "../../services/api";

import "./styles.css";

export default class Team extends Component {
    state = {
        teams: [],
        teamId: null,
    };

    componentDidMount() {
        this.loadTeamInfo();
    };

    loadTeamInfo = async () => {
        const { nickName } = this.props.match.params; 

        const response = await api.get(`teams/nickName/${nickName}`);

        const {teams} = response.data.api.teams;
        this.setState({teams: teams, teamId: teams.teamId});
    };

    loadTeamPlayers = async () => {
        const { teamId } = this.state; 

        const response = await api.get(`players/teamId/${teamId}`);

        console.log(response);
    };

    render() {
        const {teams} = this.state;

        return (
            <div className="team-page">
                {teams.map(team => (
                    <article key={team}>
                        <div className="infos">
                            <h3>{team.fullName}</h3>
                            <ul>
                                <p>Nome: {team.fullName}</p>
                                <p>Apelido: {team.nickname}</p>
                                <p>Cidade: {team.city}</p>
                                <p>Abreviação: {team.shortName}</p>
                                <div className="leagues">
                                    <p>Ligas Disputadas: </p> 
                                    <ul>{
                                        Object.keys(team.leagues).map(league => (
                                            <p key={league}>{league}</p>
                                        ))}
                                    </ul>
                                </div>
                            </ul>
                        </div>
                        <div className="logo">
                            <img alt={`logo do time ${team.fullName}`} src={team.logo}/>
                        </div>
                    </article>
                ))}
            </div>
        );
    }
};
