import React, { Component } from "react";
import api from "../../services/api";
import { Link } from "react-router-dom";

// import "./styles.css";

export default class allTeams extends Component {
    state = {
        teams: [],
    };

    componentDidMount() {
        this.loadTeams();
    };

    loadTeams = async () => {
        const { leagueName } = this.props.match.params;

        const response = await api.get(`conferences/`);
        this.setState({teams: response.data.api.teams});
        console.log(response)
    };

    render() {

        const { teams } = this.state;

        return (
            <div className="teams-page">
                <h1>Teams</h1>
                <div className="conferenceEast">
                    <div className="division-Atlantic">
                    </div>
                    <div className="division-Central">
                    </div>
                    <div className="division-Southeast">
                    </div>
                </div>
                <div className="conferenceWest">
                    <div className="division-Northwest">
                    </div>
                    <div className="division-Pacific">
                    </div>
                    <div className="division-Southwest">
                    </div>
                </div>
                    {/* <div className="division">
                        {teams.map(team => (
                            <Link to={`/team/${team.nickname}`} key={team.fullName}>
                                <strong>{team.fullName}</strong>
                                <img alt={`logo do time ${team.fullName}`} src={team.logo}/>
                            </Link>
                        ))}
                    </div> */}
                
            </div>
        );
    }
};
