import React, { Component } from "react";
import api from "../../services/api";

export default class Main extends Component {
    state = {
        ngenre: null,
        genres: [],
    };

    componentDidMount() {
        this.loadGenres();
    }

    loadGenres = async () => {
        const response = await api.get("/api.cgi",{
            params: {
                "t" : "genres"
            }
        });

        this.setState({ngenre: response.data.COUNT, genres: response.data.ITEMS})

        console.log(response.data);
    };

    render() {

        const {ngenre, genres} = this.state;

        return (
            <div className="genres-list">
                <h2>Numero de Generos: {ngenre}</h2>
                {Object.entries(genres).map(([key,value]) => (
                    <article key={key}>
                        <h2>{value}</h2>
                    </article>
                ))}
            </div>
        );
    }
};
