import React, { Component } from "react";
import api from "../../services/api";
import { Link } from "react-router-dom";

import "./styles.css";

export default class Main extends Component {
    state = {
        selecetSeason: "",
        seasons: [],
        leagues: [],
    };

    componentDidMount() {
        this.loadSeasons();
        this.loadLeagues();
    };

    loadSeasons = async () => {
        const response = await api.get("seasons/");
        this.setState({seasons: response.data.api.seasons});
    };

    loadLeagues = async () => {
        const response = await api.get("leagues/");
        this.setState({leagues: response.data.api.leagues})
    };

    

    render() {

        const { seasons, leagues, selecetSeason } = this.state;

        console.log(selecetSeason);
    
        return (
            <div className="main-page">
                <div className="seasons">
                    <h2>Seasons</h2>
                    <div className="seasons-items">
                        {seasons.map(season => (
                            <Link to={`?=${season}`} key={season} onClick={
                                () => this.setState({selecetSeason: this.props.location.search})}>
                                {season}
                            </Link>
                        ))}
                    </div>
                </div>
                <div className="leagues" disabled={selecetSeason === ""}>
                    <h2>Leagues</h2>
                    {leagues.map(league => (
                        <Link to={`/league/${league}`} key={league}>
                            {league}
                        </Link>
                    ))}
                </div>
            </div>
        );
    }
};
