import React, { Component } from "react";
import api from "../../services/api";

export default class Main extends Component {
    state = {
        data: [],
        ngenre: null,
        genres: [],
    };

    componentDidMount() {
        this.loadGenres();
    }

    loadGenres = async () => {
        const response = await api.get("/api.cgi",{
            params: {
                "t" : "genres"
            }
        });

        this.setState({ data: response.data, ngenre: response.data.COUNT, genres: response.data.ITEMS})

        console.log(response.data);
    };

    render() {

        const { data, ngenre } = this.state;

        return (
            <div className="genres-list">
                <h2>data = {data.count}</h2>
                <h2>Numero de Generos: {ngenre}</h2>
            </div>
        );
    }
};
