import React, { Component } from "react";

export default class Genres extends Component {
    state = {
        ngenre: null,
        genres: [],
    };

    componentDidMount() {
        this.loadGenres();
    }

    loadGenres = async () => {
        const response = await api.get("/api.cgi",{
            params: {
                "t" : "genres"
            }
        });

        this.setState({ngenre: response.data.COUNT, genres: response.data.ITEMS})

        console.log(response.data.ITEMS);
    };
    
    render() {
        return(
            <h2>Hello World</h2>
        )   
    }
};
